function request(function_id, body = {}, timeout = 0) {
  return new Promise(resolve => {
    setTimeout(() => {
      const options = {
        url: `https://api.m.jd.com/client.action?functionId=${function_id}`,
        body: `appid=babelh5&body=${encodeURIComponent($.toStr(body))}&sign=11&t=${+new Date()}`,
        headers: {
          "Host": "api.m.jd.com",
          "Accept": "*/*",
          "Content-Type": "application/x-www-form-urlencoded",
          "Accept-Language": "zh-CN,zh-Hans;q=0.9",
          "User-Agent": $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : (require('./USER_AGENTS').USER_AGENT)) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.4.4;14.3;network/4g;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1"),
          "Referer": "https://prodev.m.jd.com/mall/active/2HtgSSsyn1MNsuQFD88bJ7XxJxHs/index.html",
          "Accept-Encoding": "gzip, deflate, br",
          "Cookie": cookie
        }
      }
      $.post(options, (err, resp, data) => {
        try {
          if (err) {
            console.log(function_id + ' APIæŸ¥è¯¢è¯·æ±‚å¤±è´¥ â€¼ï¸â€¼ï¸')
            console.log(JSON.stringify(err));
          } else {
            data = $.toObj(data, {});
          }
        } catch (e) {
          $.logErr(e, resp);
        } finally {
          resolve(data);
        }
      })
    }, timeout)
  })
}

function TotalBean() {
  return new Promise(async resolve => {
    const options = {
      url: "https://wq.jd.com/user_new/info/GetJDUserInfoUnion?sceneval=2",
      headers: {
        Host: "wq.jd.com",
        Accept: "*/*",
        Connection: "keep-alive",
        Cookie: cookie,
        "User-Agent": $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : (require('./USER_AGENTS').USER_AGENT)) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.4.4;14.3;network/4g;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1"),
        "Accept-Language": "zh-cn",
        "Referer": "https://home.m.jd.com/myJd/newhome.action?sceneval=2&ufc=&",
        "Accept-Encoding": "gzip, deflate, br"
      }
    }
    $.get(options, (err, resp, data) => {
      try {
        if (err) {
          $.logErr(err)
        } else {
          if (data) {
            data = JSON.parse(data);
            if (data['retcode'] === 1001) {
              $.isLogin = false; //cookieè¿‡æœŸ
              return;
            }
            if (data['retcode'] === 0 && data.data && data.data.hasOwnProperty("userInfo")) {
              $.nickName = data.data.userInfo.baseInfo.nickname;
            }
          } else {
            console.log('äº¬ä¸œæœåŠ¡å™¨è¿”å›ç©ºæ•°æ®');
          }
        }
      } catch (e) {
        $.logErr(e)
      } finally {
        resolve();
      }
    })
  })
}
